actions.script:
import org.elasticsearch.action.index.IndexResponse
import org.slf4j.Logger
import org.slf4j.LoggerFactory
import org.moqui.context.ExecutionContext
ExecutionContext ec = context.ec
Logger logger = LoggerFactory.getLogger("index_DataDocuments")

documentVersionList = []
originalDocumentList = getOriginalDocuments ? [] : null
for (Map document in documentList) {
    if (getOriginalDocuments) {
        // this may fail for a number of reasons, like index doesn't exist yet, so catch the exception
        try {
            Map originalDocument = ec.elasticSearchClient
                    .prepareGet((String) document._index, (String) document._type, (String) document._id)
                    .execute().actionGet().getSourceAsMap()
            originalDocumentList.add(originalDocument ?: [:])
        } catch (Exception e) {
            logger.info("Could not get original document for [${document._index}, ${document._type}, ${document._id}]: ${e.toString()}")
            originalDocumentList.add([:])
        }
    }
    IndexResponse response = ec.elasticSearchClient
            .prepareIndex((String) document._index, (String) document._type, (String) document._id)
            .setSource(document).execute().actionGet()
    documentVersionList.add(response.getVersion())
    // logger.warn("============ indexed [${document._index}, ${document._type}, ${document._id}] version [${response.getVersion()}]")
}

/* useful docs for query API: http://www.elasticsearch.org/guide/reference/api/search/uri-request/ */
import org.elasticsearch.action.get.MultiGetItemResponse import org.elasticsearch.action.get.MultiGetRequestBuilder import org.elasticsearch.index.query.QueryBuilders import org.elasticsearch.search.SearchHit import org.elasticsearch.search.SearchHits import org.moqui.context.ExecutionContext ExecutionContext ec = context.ec
documentList = []
// get the search hits SearchHits hits = ec.elasticSearchClient.prepareSearch().setIndices(indexName).setTypes(documentType) .setQuery(QueryBuilders.queryString(queryString)).setFrom(fromOffset).setSize(sizeLimit).execute().actionGet().getHits() if (hits.getTotalHits() > 0) { MultiGetRequestBuilder mgrb = ec.elasticSearchClient.prepareMultiGet() for (SearchHit hit in hits) mgrb.add(hit.getIndex(), hit.getType(), hit.getId()) Iterator mgirIt = mgrb.execute().actionGet().iterator() while(mgirIt.hasNext()) { MultiGetItemResponse mgir = mgirIt.next() Map document = mgir.getResponse().getSourceAsMap() documentList.add(flattenDocument ? StupidUtilities.flattenNestedMap(document) : document) } }
// get the total search count documentListCount = ec.elasticSearchClient.prepareCount(indexName).setQuery(QueryBuilders.queryString(queryString)).execute().actionGet().getCount()
// calculate the pagination values documentListPageIndex = pageIndex documentListPageSize = pageSize documentListPageMaxIndex = ((BigDecimal) documentListCount).divide(documentListPageSize, 0, BigDecimal.ROUND_DOWN) as int documentListPageRangeLow = documentListPageIndex * documentListPageSize + 1 documentListPageRangeHigh = (documentListPageIndex * documentListPageSize) + documentListPageSize if (documentListPageRangeHigh > documentListCount) documentListPageRangeHigh = documentListCount
